#pragma once

/**
 * @brief The Serial class represents a serial communication interface.
 *
 * This class inherits from the IMemoryUnit interface, which allows it to read and write bytes of data.
 */
class Serial : public IMemoryUnit
{
public:
    /**
     * @brief Constructs a new Serial object.
     */
    Serial();

    /**
     * @brief Destroys the Serial object.
     */
    ~Serial();

    // IMemoryUnit

    /**
     * @brief Reads a byte of data from the specified address.
     *
     * @param address The memory address to read from.
     * @return The byte of data read from the address.
     */
    byte ReadByte(const ushort& address);

    /**
     * @brief Writes a byte of data to the specified address.
     *
     * @param address The memory address to write to.
     * @param val The value to write at the address.
     * @return True if the write operation was successful, false otherwise.
     */
    bool WriteByte(const ushort& address, const byte val);

private:
    byte m_Data;
};
